mapper.select:
select count(favno)
from favoritehouse
where userid = #{userid} and name = #{name}

select distinct d.name name, f.gugunname gugunname, d.dong dong, d.jibun jibun 
from favoritehouse f, deal_data d
where f.userid = #{userid} and f.gugunname = #{gugunname} and f.name != #{name} and f.name = d.name;
